    public function index() {

        $k = getValue('k');
        $lang = getValue('l');

        if ($k == 'app') {
            $_SESSION['isApp'] = 1;
        }

        $message = [
            L('inviteUserToGet'), L('investmentIncome')
        ];

        $fakerPhones = $this->fakerPhone();
        $fakers = [];
        for ($i = 0; $i< 100;$i++){
            $faker = [
                'phone' => $this->_hide($fakerPhones[rand(0, 2000)]),
                'cash' => rand(1000, 9999),
                'message' => $message[rand(0, 1)]
            ];

            $fakers[] = $faker;
        }


        $now = date('Y-m-d H:i:s');
        $project_class = getData('project_class', 'all', 'add_time <= \'' . $now . '\'', '', 'sort asc');
        $title = getData('info', 'all');
        //var_dump($title['0']['webname']);
        $this->assign('project_class', $project_class);
        $this->assign('title', $title['0']);
		$item = getData('item', 'all', 'time <= \'' . $now . '\'', '', 'sort asc');
		$this->assign('item', $item);
        $this->assign('fakers', $fakers);
        $this->display(isTemplate(getInfo('template'), 'index'));
    }

    private function _hide($tel)
    {
        $sub = strlen($tel) == 11 ? 4 : 5;
        $hide = strlen($tel) == 11 ? '****' : '*****';
        return substr($tel,0,strlen($tel)-$sub).$hide;
    }

