                msg('กรุณากรอกรหัสผ่าน 6-16 หลักอีกครั้ง！');
            }

            if ($pwd != $pwd2) {
                msg('รหัสผ่านทั้งสองครั้งไม่ตรงกัน！');
            }

            if ($forgetSmsCode != $code) {
                msg('รหัสยืนยันทาง SMS ไม่ถูกต้อง！');
            }

            $data = array('password' => md5($pwd), 'password2' => md5($pwd2));

            if (editData('user', $data, 'phone = \'' . $phone . '\'')) {
                msg('找回成功！');
            } else {
                msg('找回失败！');
            }
        } else {
            $this->display();
        }
    }

    public function yuebao() {
        $uid = $_SESSION['uid'];
        $res = getData('yuebao', 1, 'uid = \'' . $uid . '\'');

        $this->assign('res', $res);
        $this->display();
    }

